引用传递：std::ref()

// 函数对象
type function(type param);
/* 值传递：复制 */ thread th1(function, param);
/* 值传递：移动 */ thread th1(function, std::move(param));
type function(type& param)
/* 引用传递 */ thread th1(function, std::ref(param));

// 类对象
class className {
    className(type param):(){};
};
